mapper.select:
SELECT count(*) FROM( SELECT e.EMPNO, e.DEPTNO, d.DNAME, e.RANKNO, e.AUTHNO, e.NAME, e.EMAIL, e.HIREDATE FROM EMP e, DEPT d WHERE e.DEPTNO = d.DEPTNO(+)) WHERE 1=1
and name like '%'||#{name}||'%'
and dname like '%'||#{dname}||'%'
and authno =#{authno}
and rankno =#{rankno}

SELECT * FROM( SELECT rownum cnt,a.* FROM (SELECT e.EMPNO, e.DEPTNO, d.DNAME, er.rname, e.rankno, au.aname, e.AUTHNO, e.NAME, e.EMAIL, e.HIREDATE FROM EMP e, DEPT d, EMPRANK er, Author au WHERE e.DEPTNO = d.DEPTNO(+) AND e.rankno = er.rankno AND e.authno = au.authno ORDER BY e.EMPNO) a WHERE 1=1
and name like '%'||#{name}||'%'
and dname like '%'||#{dname}||'%'
and authno =#{authno}
and rankno =#{rankno}
) WHERE cnt BETWEEN #{start} AND #{end}

mapper.update:
UPDATE emp
SET PROFILE= #{profile}, name=#{name}, RANKNO =#{rankno}, email =#{email}, deptno=#{deptno}
WHERE EMPNO=#{empno}
