mapper.update:
UPDATE pjboard
   SET views = views+1
 WHERE pjboardno = #{pjboardno}

	UPDATE pjboard_file
		SET fname = #{tar,jdbcType=VARCHAR},
        uptdate= sysdate
    WHERE pjboardno = #{no,jdbcType=INTEGER}
	AND fname = #{org,jdbcType=VARCHAR}

mapper.select:
SELECT fname 
FROM pjboard_file		
WHERE pjboardno=#{pjboardno,jdbcType=INTEGER}
